export default (data)=>{
  const { lengthDetailShow,isLegendNumClick} = data
  const total = data.reduce((total, item) => {
    return total + item.value
}, 0)
  return {
    "legend": {
      // selectedMode:false,
      type: 'scroll',
      itemWidth: 'w(10)',
      itemHeight: 'w(10)',
      borderRadius: 'w(2)',
      icon: 'roundRect',
      y: 'center', // 图例垂直居上
      left: lengthDetailShow?'50%':'58%',
      height: data.length>=9?'90%':'100%',
      width: '100%',
      orient: 'vertical',
      align: 'auto',
      itemGap: 'h(21)',
      textStyle: {
        fontSize: 'w(14)',
        color: '#C3CED5',
        fontFamily:
          'font-family: Source Han Sans CN-Regular, Source Han Sans CN',
        rich: {
          name: {
            verticalAlign: 'right',
            align: 'left',
            width: 'w(80)',
            height: 'h(20)',
            fontSize: 'w(14)',
            color: '#C3CED5',
          },
          value: {
            align: 'left',
            color: isLegendNumClick?'#4B98FA':'#C3CED5',
            fontSize: 'w(14)',
          },
          rate: {
            align: 'left',
            width: 'w(50)',
            fontSize: 'w(14)',
            color: '#C3CED5',
          },
        },
      },
      pageTextStyle:{
          color:'#C3CED5'
      },
      pageButtonItemGap: 'w(20)',
      pageButtonGap:'w(10)',
      pageButtonPosition: 'end', // 按钮在图例的末尾位置，即底部
      pageIcons: {
          vertical: [
              'image://data:image/svg+xml;base64,',
              'image://data:image/svg+xml;base64,',
          ]
      },

      data: data.map((item) => item.name),
      formatter: function (name) {
        let tarValue
        for (var i = 0; i < data?.length; i++) {
          if (name === data?.[i]?.name) {
            tarValue = data?.[i]?.value
          }
        }

        let p = Math.round((tarValue / total) * 100)
        const detail = '{rate|      ' + p+'%' + '}' + '{value|      ' + tarValue + '}'
        return '{name| ' + name + '}' + (lengthDetailShow?detail:'')
      },
    },
  tooltip: {
      // trigger: "axis",
      backgroundColor: 'rgba(130,174,255,0.2)',
      borderWidth: 0,
      className: 'tooltip-back-bar',
      textStyle: {
          color: ' #E8F3FF',
          fontSize: 'w(11)',
          fontFamily: 'Source Han Sans CN-Bold, Source Han Sans CN',
          fontWeight: 'bold',
      },
      formatter: function (params) {
          if (params.seriesName == 1 || params.seriesName == 2) {
              return params.marker + params.name + '：' + params.value
          }
      },
  },
  title: {
      show: false,
      text: '75.53%',
      left: 'center',
      top: '32%',
      textStyle: {
          textAlign: 'center',
          color: '#ffffff',
          fontSize: 'w(14)',
          fontWeight: 600,
      },
  },
  
      series: [
        {
          name: '1',
          type: 'pie',
          center: ['32%', '50%'],
          radius: ['h(106)', 'h(124)'],
          avoidLabelOverlap: true,
          // color:['rgba(254, 225, 134, 1)','rgba(95, 213, 236, 1)','rgba(189, 219, 255, 1)','rgba(83, 240, 146, 1)','rgba(54, 134, 255, 1)'],
          label: {
            show: false,
          },
          itemStyle: {
            color: (params) => {
              let colorList = [
                '#FEE186',
                '#5FD5EC',
                '#BDDBFF',
                '#3686FF',
                '#5FECB9',
                '#DEBDFF',
              ]
              return colorList[params.dataIndex]
            },
            // borderRadius: 0,
            // borderColor: '#fff',
            // borderWidth: 5
          },
          emphasis: {
            label: {
              show: false,
            },
          },
          labelLine: {
            show: false,
          },
          data: data,
        },
        {
          // tooltip:{
          //   show:false
          // },
          name: '2',
          type: 'pie',
          center: ['32%', '50%'],
          radius: ['h(84)', 'h(106)'],
          avoidLabelOverlap: true,
          labelLayout: {
            hideOverlap: false,
          },
          label: {
            show: false,
          },
          itemStyle: {
            color: (params) => {
              let colorList = [
                'RGBA(66, 75, 90, 1)',
                'RGBA(34, 73, 110, 1)',
                'RGBA(53, 74, 114, 1)',
                'RGBA(53, 74, 114, 1)',
                'RGBA(26, 57, 114, 1)',
                'RGBA(53, 74, 114, 1)',
              ]
              return colorList[params.dataIndex]
            },
          },
          emphasis: {
            disabled: true,
            label: {
              show: false,
            },
          },
          labelLine: {
            show: false,
          },
          data: data,
        },
        {
          name: '3',
          type: 'pie',
          center: ['32%', '50%'],
          radius: ['h(32)', 'h(42)'],
          avoidLabelOverlap: true,
          labelLayout: {
            hideOverlap: false,
          },
          label: {
            show: true,
            position: 'inside',
            formatter: function (d) {
              return parseFloat(d.percent).toFixed(0) + '%'
            },
            color: '#C3CED5',
            fontSize: 'w(14)',
          },
          itemStyle: {
            color: 'rgba(19, 38, 79, 0)',
          },
          emphasis: {
            disabled: true,
            label: {
              show: false,
            },
          },
          labelLine: {
            show: false,
          },
          data: data,
        },
      ],
    }
}